import React, {useState, useEffect} from 'react'
import {useParams,Link} from 'react-router-dom'
import axios from 'axios'
import {showErrMsg, showSuccessMsg} from '../../utils/notification/Notification'


function ActivationEmail() {
    const {activation_token} = useParams()
    const [err, setErr] = useState('')
    const [success, setSuccess] = useState('')

    useEffect(() => {
        if(activation_token){
            const activationEmail = async () => {
                try {
                    const res = await axios.post('/user/activation', {activation_token})
                    setSuccess(res.data.msg)
                } catch (err) {
                    err.response.data.msg && setErr(err.response.data.msg)
                }
            }
            activationEmail()
        }
    },[activation_token])

    return (
        <div className="active_page">
           
            {success && showSuccessMsg(success) ? 
            <div>
             <div className="container d-flex justify-content-center">
             <div className="row">
                 <div className="col-md-6"> 
                 <button type="button" className="btn btn-lg btn-warning" data-toggle="modal" data-target="#myModal">Open Congratulations card Modal</button> 
                 </div>
             </div>
             </div>
         <div className="modal fade" id="myModal" role="dialog">
             <div className="modal-dialog">
                 <div className="card">
                     <div className="text-right cross"> <i className="fa fa-times"></i> </div>
                     <div className="card-body text-center"> <img src="https://img.icons8.com/bubbles/200/000000/trophy.png"/>
                         <h4>CONGRATULATIONS!</h4>
                         <p>You have been personally selected to take part in our 2017 annual visitors survey!</p> 
                         <button className="btn btn-out btn-square continue">CONTINUE</button>
                     </div>
                 </div>
             </div>
         </div>
         </div>
             : err && showErrMsg(err) }
           
        </div>
    )
}

export default ActivationEmail
