import React, {useState, useEffect} from 'react'
import axios from 'axios'
import {useSelector, useDispatch} from 'react-redux'
import {isEmpty} from '../../utils/validation/Validation'
import {showSuccessMsg, showErrMsg} from '../../utils/notification/Notification'

import { dispatchGetAllRapports, fetchAllRapports } from '../../../redux/actions/rapportsAction'

const initialState = {
    title: '',
    link: '',
    err: '',
    success: ''
}
function UploadRap() {
    const [rapport, setRapport] = useState(initialState)

    const {title, link, err, success} = rapport
    

    const handleChangeInput = e => {
        const {title, value} = e.target
        setRapport({...rapport, [title]:value, err: '', success: ''})
    }

}
function Library() {
    const auth = useSelector(state => state.auth)
    const token = useSelector(state => state.token)
    const rapports = useSelector(state => state.rapports)

    const {rapport, isAdmin} = auth
    const [data, setData] = useState(initialState)
    const {err, success} = data

    const [loading, setLoading] = useState(false)
    const [callback, setCallback] = useState(false)

    const dispatch = useDispatch()

  useEffect(() => {
        if(isAdmin){
            fetchAllRapports(token).then(res =>{
                dispatch(dispatchGetAllRapports(res))
            })
        }
    },[token, isAdmin, dispatch, callback])

    const changeRapport = async(e) => {
        e.preventDefault()
        try {
            const file = e.target.files[0]

            if(!file) return setData({...data, err: "No files were uploaded." , success: ''})

            if(file.size > 1024 * 1024)
                return setData({...data, err: "Size too large." , success: ''})

            if(file.type !== 'application/pdf')
                return setData({...data, err: "File format is incorrect." , success: ''})

            let formData =  new FormData()
            formData.append('file', file)

            setLoading(true)
            const res = await axios.post('/api/upload_rapport', formData, {
                headers: {'content-type': 'multipart/form-data', Authorization: token}
            })

            setLoading(false)            
        } catch (err) {
            setData({...data, err: err.response.data.msg , success: ''})
        }
    }

    const handleDelete = async (id) => {
        try {
            if(rapport._id !== id){
                if(window.confirm("Are you sure you want to delete this rapport?")){
                    setLoading(true)
                    await axios.delete(`/rapport/deleteRapport/${id}`, {
                        headers: {Authorization: token}
                    })
                    setLoading(false)
                    setCallback(!callback)
                }
            }
            
        } catch (err) {
            setData({...data, err: err.response.data.msg , success: ''})
        }
    }
    const handleSubmit = async e => {
        e.preventDefault()
        if(isEmpty(title) || isEmpty(link))
                return setRapport({...rapport, err: "Please fill in all fields.", success: ''})

        try {
            const res = await axios.post('/rapport/uploadRap', {
                title,link
            })

            setRapport({...rapport, err: '', success: res.data.msg})
        } catch (err) {
            err.response.data.msg && 
            setRapport({...rapport, err: err.response.data.msg, success: ''})
        }
    }
    return (
        <>
        <div>
            {err && showErrMsg(err)}
            {success && showSuccessMsg(success)}
            {loading && <h3>Loading.....</h3>}
        </div>
        <div className="profile_page">
            <div className="col-left">
                <h2>{isAdmin ? "Admin Profile": "User Profile"}</h2>
                <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="title">Title</label>
                    <input type="text" placeholder="Title" id="title"
                        name="title" />
                </div>

                <div>
                    <span>
                    <i className="fas fa-upload">
                        <p>Upload</p>
                        <input type="file" name="link" id="link" onChange={changeRapport} />
                        </i>
                        </span>
                </div>
                <button disabled={loading}>Confirm</button>
                </form>
            </div>
            {isAdmin ? 
            <div className="col-right">
                <h2>Rapports List </h2>

                <div style={{overflowX: "auto"}}>
                <table className="customers">
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>Title</th>
                                <th>Link</th>
                                <th>Action</th>
                            </tr>
                        </thead>
                      
                        <tbody>
                            {
                                rapports.map(rapport => (
                                    <tr key={rapport._id}>
                                        <td>{rapport._id}</td>
                                        <td>{rapport.title}</td>
                                        <td>{rapport.link}</td>
                                        <td>
                                            <i className="fas fa-trash-alt" title="Remove"
                                            onClick={() => handleDelete(rapport._id)} ></i>
                                        </td>
                                    </tr>
                                ))
                            }
                        </tbody>
                    </table>
                </div>
            </div>
             : ""}
        </div>
       
        </>
    )
}

export default  Library
